<template>    <rs-section>        <div class="tabs">            <ul class="d-flex flex-row overflow-auto text-center" :class="[`tabs-${tabsPosition}`]">                <li v-if="tab.hasOwnProperty('visibility') ? tab.visibility : true" :class="[{'flex-grow-1' : fullTabWidth}, {'active' : index === selectedTab}]"                    v-for="(tab, index) of tabs"                    :key="index"                    @click="model = index">                    <span class="tabs-tab"                          :class="[{'active' : index === selectedTab}, {'disabled' : tab.hasOwnProperty(tab.enabled) ? !tab.enabled : false}]"                          :id="tab.name">                        <img v-if="tab.hasOwnProperty('icon') && typeof tab.icon === 'string'" :src="getIconTab(index)"                             alt="Icon" class="me-10"/>                        <span class="text-uppercase text-nowrap">{{ tab.label }}</span>                    </span>                </li>            </ul>            <hr class="my-0 opacity-1" style="border: none; border-top: solid 1px" />            <div>                <slot/>            </div>        </div>    </rs-section></template><script>    export default {        name: "RSTabs",        model: {            prop: 'selectedTab',            event: 'change'        },        props: {            fullTabWidth: {                default: false,                type: Boolean,                required: false            },            tabs: {                default: () => ({                    /*                     * Default template:                     * [                     *    {                     *       content: '...',                     *       enabled: true|false,                     *       icon: '...'|null|undefined,                     *       activeIcon: '...'|null|undefined,                     *       label: '...',                     *       name: '...',                     *       visibility: true|false                     *    },                     *    ...                     * ]                     */                    default: () => ([]),                    type: Array,                    required: true                })            },            tabsPosition: {                default: 'top',                type: String,                required: false            },            selectedTab: {                default: 0,                type: Number,                required: false            }        },        data: () => ({}),        computed: {            model: {                get() {                    return this.selectedTab                },                set(selectedTab) {                    this.$emit('change', selectedTab)                }            },        },        methods: {            getIconTab(position) {                if (this.selectedTab === position) {                    return (this.tabs[position].hasOwnProperty('activeIcon') && typeof this.tabs[position].activeIcon === 'string')                        ? this.tabs[position].activeIcon                        : ((this.tabs[position].hasOwnProperty('icon') && typeof 'icon' === 'string')                            ? this.tabs[position].icon : '')                }                else if (this.tabs[position].hasOwnProperty('icon') && typeof this.tabs[position].icon === 'string')                    return this.tabs[position].icon;                return ''            }        }    }</script><style lang="stylus">    @import "../../assets/styles/modules/restyle/tabs.styl"</style>